        public static DataTable GetExcelToDataTableBySheet(string FileFullPath)
        {
            
            string strConn= "Provider=Microsoft.Ace.OleDb.12.0;" + "Data Source=" + FileFullPath + ";Extended Properties='Excel 12.0; HDR=NO; IMEX=1';";
//此连接可以操作.xls与.xlsx文件
            OleDbConnection conn = new OleDbConnection(strConn);
            conn.Open();
            DataSet ds = new DataSet();
            OleDbDataAdapter odda = new OleDbDataAdapter(" SELECT * FROM [Sheet1$] ", conn);                   
            //("select* from [Sheet1$]", conn);
            odda.Fill(ds, " SELECT * FROM [Sheet1$] ");
            conn.Close();
            return ds.Tables[0];
        }    
  
